						//$('.count2').addClass('count');
						$({ Counter: 0 }).animate({
							Counter: $('.count2').text()
						}, {
							duration: 1000,
							easing: 'swing',
							step: function() {
							$('.Single').text(Math.ceil(this.Counter));
							}
						});
					}

			});
		}

		function getLocation() {
			//showPosition('');
				navigator.geolocation.getCurrentPosition(function(position) {
					showPosition(position)
				},
				function(error) {

					showPosition('');
				});

		}

	    const submitGetUpdate = event => {
			event.preventDefault();
			event.target.submit.innerHTML = `<div class="spinner-grow text-dark" role="status"><span class="visually-hidden">Loading...</span></div>`;
			const fd = new FormData(event.target);
			commonAjax({
				page: '/php/module/other/getupdate.php',
				params: fd
			})
			.then(response => {
				event.target.submit.innerHTML = "<span>Submit</span>";
				snackbar({success: response.success, message: response.message});
				if(response.success)
				{
					event.target.reset();
					$("#exampleModal").modal("hide");
				}
			})
			.catch(err => {console.log(err); event.target.submit.innerHTML = "<span>Submit</span>";})
		}
		document.forms.getupdate.addEventListener("submit", submitGetUpdate);
	</script>

